mapper.select:
select
,
from content where id = #{id,jdbcType=BIGINT}

select c.id, c.uid,c.title, c.forward_cid, c.feeling, c.type, c.conver_image, c.forward_title, c.forward_url, c.content_type, c.thumbnail, c.hot_value, c.like_count,c.review_count,c.person_count,c.favorite_count, c.read_count, c.read_count_dummy ,c.authorization, c.create_time, c.status,c.content,c.rights from content c
left join topic t on c.forward_cid = t.id
where c.status = 0 and (c.uid = #{uid,jdbcType=BIGINT}
or find_in_set(#{uid,jdbcType=BIGINT},SUBSTR(t.core_circle FROM 2 FOR LENGTH(t.core_circle)-2))
)
and c.rights = 1
and c.type != 3
and c.type != 6
and c.type = 3 and t.long_time < #{updateTime,jdbcType=BIGINT}
and c.id < #{sinceId}
order by t.long_time desc limit 10
order by c.id desc limit 10

select id,uid,live_image as coverImage,title as title,status as status,create_time as createTime,review_count as reviewCount,like_count as likeCount  from topic_count_view  where 1=1

  and create_time >= #{startDate,jdbcType=TIMESTAMP}


  and create_time <= #{endDate,jdbcType=TIMESTAMP}


  and uid = #{uid,jdbcType=BIGINT}


  and review_count >= #{reviewCount,jdbcType=BIGINT}


  and like_count >= #{likeCount,jdbcType=BIGINT}

select m.*,h.id as hid FROM (select c.id, c.uid,c.title, c.forward_cid, c.feeling, c.type, c.conver_image, c.forward_title, c.forward_url, c.content_type, c.thumbnail, c.hot_value, c.like_count, c.review_count,c.person_count,c.favorite_count, c.read_count, c.read_count_dummy ,c.authorization, c.create_time, c.status,c.content,c.rights from content c LEFT JOIN topic t on c.forward_cid=t.id where c.status != 1 and c.rights=1
and c.type in (0,3) and (t.type is null or t.type!=1000)
and c.type=3 and t.type=1000
) m,high_quality_content h where m.id = h.cid and h.id < #{sinceId} order by h.id desc limit #{pageSize}

select
  ,
  
 from (select ,
 from content where uid IN (SELECT target_uid from user_follow where source_uid = #{uid} and target_uid != 0) and `status` != 1 and rights = 1

and type != 6

UNION
select
,

from content where uid = #{uid} AND `status` != 1

and type != 6

) v where v.id < #{sinceId}
order by v.id desc limit 10;
