mapper.select:
select

from user_profile p
where  p.uid in (SELECT f.source_uid FROM user_follow f WHERE f.target_uid = #{uid,jdbcType=BIGINT})
and  p.nick_name like #{nickName,jdbcType=VARCHAR}
order by convert(nick_name USING gbk) COLLATE gbk_chinese_ci limit #{start,jdbcType=INTEGER} ,#{pageSize,jdbcType=INTEGER}
